let sketch = function (p) {

    let objs = [];
    let lastC,
        buttonPressed;

    const frameDelay = 10;
    let lastPressedFrame = 0;

    p.setup = function () {
        let cnv = p.createCanvas(p.windowWidth, p.windowHeight);
        cnv.id('introcanvas');
        // p.background(100, 150, 255);
        p.background(126);
        objs.push(new Thing(randomEmoji()));
    }

    p.draw = function () {
        p.background(p.calcBackground());
        for (obj of objs) {
            obj.update();
            obj.draw();
        }
        p.fill(255);
    }

    p.addEmoji = function () {
        if (objs.length > 100) return null;
        if (lastPressedFrame + frameDelay > p.frameCount) return null;
        var th = new Thing(randomEmoji())
        objs.push(th);
        lastPressedFrame = p.frameCount;
        return th;
    }

    p.calcBackground = function () {
        p.colorMode(p.HSB);
        if (p.mouseX < -1 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) {
            return lastC
        }
        let h = p.map(p.mouseX, 0, p.width, 0, 360),
            s = 75,
            b = p.map(p.mouseY, 0, p.height, 50, 100);
        // let c = p.color(100, 100, 100);
        let c = p.color(h, s, b);
        let rgb = hsbToRgb(h, s + 25, p.map(b, 50, 100, 100, 50));
        // let rp = Math.max(rgb.r, rgb.g, rgb.b) + Math.min(rgb.r, rgb.g, rgb.b) - rgb.r,
        //     gp = Math.max(rgb.r, rgb.g, rgb.b) + Math.min(rgb.r, rgb.g, rgb.b) - rgb.g,
        //     bp = Math.max(rgb.r, rgb.g, rgb.b) + Math.min(rgb.r, rgb.g, rgb.b) - rgb.b

        let rp = rgb.r,
            bp = rgb.b,
            gp = rgb.g

        $('.floatingbutton').css('background-color', 'rgba(' + rp + ',' + gp + ',' + bp + ')');

        lastC = c;
        return c
    }

    p.windowResized = function () {
        p.resizeCanvas(p.windowWidth, p.windowHeight);
        p.background(100, 150, 255);
    }

    p.mousePressed = function () {
        if (p.mouseX < -1 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) {
            return;
        }
        setTimeout( () => {
            let em = p.addEmoji()
            if (em){
                em.position = p.createVector(p.mouseX, p.mouseY);
            }
        }, 200)
    }

    class Thing {
        constructor(t) {
            this.emj = t;
            this.pos = p.createVector(p.random(2, p.width - 2), p.random(2, p.height - 2));
            this.vel = p.createVector(p.random(0.5, 2) * (p.random() < 0.5 ? 1 : -1), p.random(0.5, 2) * (p.random() < 0.5 ? 1 : -1));
            this.size = 30;
        }

        set position(vector) {
            this.pos = vector;
        }

        update() {
            this.pos.add(this.vel);
            if (this.pos.x > p.width || this.pos.x < 0) {
                this.vel.x *= -1;
                this.size ++;
            }
            if (this.pos.y > p.height || this.pos.y < 0) {
                this.vel.y *= -1;
                this.size ++;
            }
            if (this.size > 60) this.size --
        }

        draw() {
            p.textSize(this.size);
            p.textAlign(p.CENTER, p.CENTER)
            p.text(this.emj, this.pos.x, this.pos.y);
        }
    }

}

function hsbToRgb(h, s, v) {
    h = lmap(h, 0, 360, 0, 1);
    s = lmap(s, 0, 100, 0, 1);
    v = lmap(v, 0, 100, 0, 1);
    var r, g, b, i, f, p, q, t;
    if (arguments.length === 1) {
        s = h.s, v = h.v, h = h.h;
    }
    i = Math.floor(h * 6);
    f = h * 6 - i;
    p = v * (1 - s);
    q = v * (1 - f * s);
    t = v * (1 - (1 - f) * s);
    switch (i % 6) {
        case 0: r = v, g = t, b = p; break;
        case 1: r = q, g = v, b = p; break;
        case 2: r = p, g = v, b = t; break;
        case 3: r = p, g = q, b = v; break;
        case 4: r = t, g = p, b = v; break;
        case 5: r = v, g = p, b = q; break;
    }
    return {
        r: Math.round(r * 255),
        g: Math.round(g * 255),
        b: Math.round(b * 255)
    };
}

let instance = new p5(sketch, 'canvasdiv');

function lmap(value, low1, high1, low2, high2) {
    return low2 + (high2 - low2) * (value - low1) / (high1 - low1);
}

